/**
 * MUVideo playlist needle information.
 *
 * @param none
 *
 * @return string with short usage description
 */
function MUVideo_needleapi_playlist_baseInfo()
{
    $info = array(
        // module name
        'module'  => 'MUVideo',
        // possible needles
        'info'    => 'MUVIDEO{PLAYLISTS|PLAYLIST-playlistId}',
        // whether a reverse lookup is possible, needs MUVideo_needleapi_playlist_inspect() function
        'inspect' => false
    );

    return $info;
}
